from datetime import datetime
from datetime import timedelta

parametros = {
    "lat": -26.9,
    "lon": 48.5,
    "n": 50
}

response = requests.get("http://api.open-notify.org/iss-pass.json", params=parametros)

resposta = response.json()
passagens = resposta['response']
print("Próximas passagens da ISS para observar em lat:", resposta['request']['latitude'], "e log:", resposta['request']['longitude'])
for passagem in passagens:
    ts = passagem['risetime']
    dt = datetime.fromtimestamp(ts)
    dtI = dt + timedelta(hours=-3)

    ts = passagem['risetime'] + passagem['duration']
    dt = datetime.fromtimestamp(ts)
    dtF = dt + timedelta(hours=-3)

    if dtI.hour < 5 or dtI.hour > 20:
      print("Início:",dtI,"-- Fim:", dtF)

